const securityTransitions = [
  {
    from: [
      SECURITY_LEVELS.LOW,
      SECURITY_LEVELS.LOW,
      SECURITY_LEVELS.LOW
    ],
    to: [
      SECURITY_LEVELS.HIGH,
      SECURITY_LEVELS.HIGH,
      SECURITY_LEVELS.HIGH
    ],
    name: "low-to-high"
  },
  {
    from: [
      SECURITY_LEVELS.HIGH,
      SECURITY_LEVELS.HIGH,
      SECURITY_LEVELS.HIGH
    ],
    to: [
      SECURITY_LEVELS.HIGH,
      SECURITY_LEVELS.LOW,
      SECURITY_LEVELS.MODERATE
    ],
    name: "high-to-mixed"
  }
];
securityTransitions.forEach((transition, index) => {
  const logArgs = [
    `Testing transition ${transition.name} (${index + 1}/${securityTransitions.length})`
  ];
  cy.log(logArgs[0]);
  cy.setSecurityLevels(
    transition.from[0],
    transition.from[1],
    transition.from[2]
  );
  cy.wait(1e3);
  if (index === 0) {
    cy.screenshot(`transition-initial-${transition.name}`);
  }
  findImportantWidgets().then((widgets) => {
    captureWidgetStates(widgets).then((initialStates) => {
      const changeLogArgs = [
        `Changing to security levels: ${transition.to.join(", ")}`
      ];
      cy.log(changeLogArgs[0]);
      cy.setSecurityLevels(
        transition.to[0],
        transition.to[1],
        transition.to[2]
      );
      cy.wait(1e3);
      if (index === 0) {
        cy.screenshot(`transition-after-${transition.name}`);
      }
      findImportantWidgets().then((newWidgets) => {
        const foundLogArgs = [
          `Found ${newWidgets.length} widgets after transition`
        ];
        cy.log(...foundLogArgs);
        expect(newWidgets.length).to.be.at.least(
          Math.max(1, widgets.length - 1),
          "Number of widgets shouldn't decrease significantly during transitions"
        );
        cy.wrap(newWidgets).each(($widget, i) => {
          const id = $widget.attr("data-testid") || `widget-${i}`;
          checkForProblematicContent($widget);
          if (initialStates[id]) {
            compareWidgetContent($widget, initialStates[id], id);
          }
        });
      });
    });
  });
});

findWidgetFlexibly(widgetTestId).then(($widget) => {
  if ($widget.length === 0) {
    cy.log(`Widget ${widgetName} not found - test will be skipped`);
    cy.screenshot(`missing-widget-${widgetTestId}`);
    expect(true).to.equal(true);
    return;
  }
  const widgetText = $widget.text();
  let foundPattern = false;
  let matchedPattern = "";
  contentPatterns2.forEach((pattern) => {
    if (typeof pattern === "string") {
      if (widgetText.includes(pattern)) {
        foundPattern = true;
        matchedPattern = pattern;
      }
    } else if (pattern.test(widgetText)) {
      foundPattern = true;
      matchedPattern = pattern.toString();
    }
  });
  if (foundPattern) {
    cy.log(`Found expected content pattern: ${matchedPattern}`);
  } else {
    cy.log(
      `No expected content patterns found in widget text: ${widgetText.substring(
        0,
        100
      )}...`
    );
  }
  expect(
    foundPattern,
    `Widget should contain at least one expected content pattern`
  ).to.be.true;
  captureWidgetScreenshot($widget.first(), `${widgetTestId}-baseline`);
});

findWidgetFlexibly(widgetTestId).then(($widget) => {
  if ($widget.length === 0) {
    cy.log(`Widget ${widgetName} not found - test will be skipped`);
    cy.screenshot(`missing-widget-${widgetTestId}-security-change`);
    expect(true).to.equal(true);
    return;
  }
  const initialContent = $widget.text();
  if (initialContent.trim().length === 0) {
    cy.log(
      `Widget ${widgetName} has no initial content - test will be skipped`
    );
    expect(true).to.equal(true);
    return;
  }
  cy.setSecurityLevels(
    SECURITY_LEVELS.LOW,
    SECURITY_LEVELS.LOW,
    SECURITY_LEVELS.LOW
  );
  cy.wait(1e3);
  findWidgetFlexibly(widgetTestId).then(($lowWidget) => {
    if ($lowWidget.length === 0) {
      cy.log(
        `Widget ${widgetName} disappeared after security level change - test will be skipped`
      );
      expect(true).to.equal(true);
      return;
    }
    const lowContent = $lowWidget.text();
    cy.setSecurityLevels(
      SECURITY_LEVELS.HIGH,
      SECURITY_LEVELS.HIGH,
      SECURITY_LEVELS.HIGH
    );
    cy.wait(1e3);
    findWidgetFlexibly(widgetTestId).then(($highWidget) => {
      if ($highWidget.length === 0) {
        cy.log(
          `Widget ${widgetName} disappeared after high security level change - test will be skipped`
        );
        expect(true).to.equal(true);
        return;
      }
      const highContent = $highWidget.text();
      if (lowContent === highContent) {
        cy.log(
          `Warning: Content did not change between low and high security levels`
        );
      }
      const contentChanged = initialContent !== lowContent || lowContent !== highContent;
      expect(contentChanged, "Content should change with security levels").to.be.true;
    });
  });
});

