// Check for critical errors
cy.window().then((win) => {
    const errors = win.consoleErrors || [];
    const criticalErrors = errors.filter((msg) => msg.toLowerCase().includes("error") &&
        !msg.includes("Warning") &&
        !msg.includes("â„¹"));
    if (criticalErrors.length > 0) {
        cy.log(`âš  Found ${criticalErrors.length} console errors`);
